dict.string:
tell application "System Events" key code 123 using {option down}
# prevent double opt trigger key code 124 delay 0.15 key code 123
key code 124 using {option down, shift down} end tell

tell application "System Events" 
	key code 124 using {shift down}
	delay 0.03
	keystroke "x" using {command down}
	delay 0.03
	key code 123
	delay 0.03
	keystroke "v" using {command down}
end tell

function run() {

	const pwlength = parseInt("{query}");
    var result           = '';
    const characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    for ( var i = 0; i < pwlength; i++ ) {
      result += characters.charAt(Math.floor(Math.random() * characters.length));
   }
   return result;

}
