input = "{query}";
var games = input.split(";");

let game_array = []; //array for elements of script filter

games.forEach(element => { //foreach item in list
  temp = element.split(","); //split name and id of the game
  game_id = "Steam ID: " + temp[0];
  game_name = temp[1];
  game_array.push ({ // Add to empty array
    'title': game_name, 
    'subtitle': game_id, 
    'arg': element 
  }) 
});

JSON.stringify({ 'items': game_array })// Transform it into JSON Alfred understands
